note.query:
DECLARE @return SMALLINT;
;WITH DailySales as( SELECT Calendar_DT ,SUM(sal.Sales_AMT) AS Sales_AMT FROM dm.Fct_YH_Sales_Inventory sal WITH(NOLOCK) GROUP BY Calendar_DT ) SELECT @return = CASE WHEN ISNULL(SUM(sal.Sales_AMT),0)/14*0.7>ISNULL(MAX(sal2.Sales_AMT),0) THEN 1 ELSE 0 END FROM [dm].[Dim_Calendar] dt WITH(NOLOCK) LEFT JOIN DailySales AS sal ON dt.Datekey >= sal.Calendar_DT AND DATEDIFF(DAY,CAST(sal.Calendar_DT AS VARCHAR),dt.Date)<14 LEFT JOIN DailySales AS sal2 ON dt.Datekey = sal2.Calendar_DT WHERE dt.Datekey>='20180801' AND dt.Date<GETDATE()AND dt.Datekey = (SELECT MAX(sal3.Calendar_DT) FROM DailySales sal3 WHERE sal3.Sales_AMT>0) --GROUP BY dt.Date_ID
SELECT @return as 'Return';
