declare @Return int;
SELECT @Return=SUM(COUNT) FROM (SELECT (7-COUNT(DISTINCT [DateKey])) COUNT FROM [Foodunion].[dm].[Fct_Qulouxia_Store_List] WHERE [DateKey]>=CONVERT(VARCHAR(10),DATEADD(DAY,-7,GETDATE()),112) UNION SELECT (7-COUNT(DISTINCT [DateKey])) FROM [Foodunion].[dm].[Fct_Qulouxia_Sales] WHERE [DateKey]>=CONVERT(VARCHAR(10),DATEADD(DAY,-7,GETDATE()),112) UNION SELECT (7-COUNT(DISTINCT [DateKey])) FROM [Foodunion].[dm].[Fct_Qulouxia_StoreInventory] WHERE [DateKey]>=CONVERT(VARCHAR(10),DATEADD(DAY,-7,GETDATE()),112) ) T
SELECT @return ;
